import { addItem } from './cart-actions';

const INITIAL_STATE = {
	cartItem: []
};

const cartReducer = (state = INITIAL_STATE, addItem) => {
	switch (addItem.type) {
		case ADD_ITEM:
			return {
				...state,
				cartItem: [ ...state.cartItem, addItem.payload ]
			};

		default:
			return state;
	}
};

export default cartReducer;
