import { addItemToCart } from './cart-util';

const INITIAL_STATE = {
	cartItem: []
};

const cartReducer = (state = INITIAL_STATE, action) => {
	switch (action.type) {
		case 'ADD_ITEM':
			return {
				...state,
				cartItem: addItemToCart(state, action.payload)
			};

		default:
			return state;
	}
};

export default cartReducer;
