import React, { Component } from "react";
import { connect } from "react-redux";

const  CartDropdown = () => {
 
    return (
      <div>
        {this.props.items.map((item) => (
          <h2>{item.name}</h2>
        ))}
      </div>
    );
  
}

const mapStateToProps = (state) => ({
  cartItems: state.cart.items,
});

export default connect(mapStateToProps)(CartDropdown);
